import { useState } from 'react';
import UserForm from './components/UserForm/UserFrom';

const App = () => {
  const [setCourseGoals] = useState([
    { text: 'Apprendre React', id: 'g1' },
    { text: "Développer plein d'applications !", id: 'g2' },
  ]);

  const addGoalHandler = (enteredText) => {
    setCourseGoals((prevGoals) => {
      const updatedGoals = [...prevGoals];
      updatedGoals.unshift({ text: enteredText, id: Math.random() });
      return updatedGoals;
    });
  };

  let content = (
    <p style={{ textAlign: 'center' }}>Pas encore d'objectif ? Ajoutez-en un</p>
  );

  return (
    <>
      <section id="goal-form">
        <UserForm onAddGoal={addGoalHandler} />
      </section>
      <section id="goals">{content}</section>
    </>
  );
};

export default App;
