import { useState } from 'react';
import UserForm from './components/UserForm/UserFrom';
import UserGoalList from './components/UserGoalList/UserGoalList';

const App = () => {
  const [courseGoals, setCourseGoals] = useState([
    { text: 'Apprendre React', id: 'g1' },
    { text: "Développer plein d'applications !", id: 'g2' },
  ]);

  const addUserHandler = (enteredText) => {
    setCourseGoals((prevGoals) => {
      const updatedGoals = [...prevGoals];
      updatedGoals.unshift({ text: enteredText, id: Math.random() });
      return updatedGoals;
    });
  };

  const deleteItemHandler = (goalId) => {
    setCourseGoals((prevGoals) => {
      const updatedGoals = prevGoals.filter((goal) => goal.id !== goalId);
      return updatedGoals;
    });
  };

  let content = (
    <p style={{ textAlign: 'center' }}>Pas encore d'objectif ? Ajoutez-en un</p>
  );

  if (courseGoals.length > 0) {
    content = (
      <UserGoalList items={courseGoals} onDeleteItem={deleteItemHandler} />
    );
  }

  return (
    <>
      <section id="goal-form">
        <UserForm onAddGoal={addUserHandler} />
      </section>

      <section id="goals">{content}</section>
    </>
  );
};

export default App;
